// Facebook Webhook Verification
const challenge = $json.query['hub.challenge'];
const verify_token = $json.query['hub.verify_token'];

if (verify_token === 'YOUR_VERIFY_TOKEN') {
  return [{
    json: { 
      challenge: parseInt(challenge),
      status: 'verified'
    }
  }];
}

return [{
  json: { 
    error: 'Invalid verify token',
    status: 'failed' 
  }
}];

// Process Facebook Messenger Message
const body = $json.body;

if (body && body.entry) {
  const messages = [];

  body.entry.forEach(entry => {
    entry.messaging?.forEach(event => {
      if (event.message && event.message.text) {
        messages.push({
          sender_id: event.sender.id,
          message_text: event.message.text,
          timestamp: event.timestamp,
          page_id: entry.id
        });
      }
    });
  });

  if (messages.length > 0) {
    return messages;
  }
}

return [{
  json: {
    status: 'no_message',
    info: 'No text message found in webhook'
  }
}];

